<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>反派逆袭系统指南 状态</title>
    <style>
        :root {
            --primary: #4f6ef7;
            --accent-rose: #ff6b9d;
            --accent-teal: #4ecdc4;
            --accent-lilac: #a29bfe;
            --surface: #f9f9fd;
            --surface-strong: #ffffff;
            --border: rgba(79, 110, 247, 0.12);
            --text-main: #2f3545;
            --text-muted: #6f7390;
        }
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }
        body {
            font-family: "Microsoft YaHei", "PingFang SC", "Hiragino Sans GB", sans-serif;
            background: transparent;
            color: var(--text-main);
            padding: 14px;
        }
        .status-card {
            max-width: 960px;
            margin: 0 auto;
            background: rgba(255, 255, 255, 0.96);
            border-radius: 20px;
            border: 1px solid var(--border);
            box-shadow: 0 18px 40px rgba(79, 110, 247, 0.12);
            padding: 20px;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
    </style>
</head>
<body>
<div class="status-card" id="status-card">
    <!-- 反派值 -->
    <div class="metric-card">
        <div class="metric-header">
            <span class="metric-label">反派值</span>
            <span class="metric-value">
                <span id="fanpaizhi">0</span>
            </span>
        </div>
        <div class="progress-bar-container">
            <div id="fanpaizhi-bar" class="progress-bar-value" style="width: 0%;"></div>
        </div>
    </div>

    <!-- 物品和技能 -->
    <div class="info-grid">
        <div class="info-card">
            <span class="info-label">拥有物品</span>
            <span class="info-value" id="wupin">N/A</span>
        </div>
        <div class="info-card">
            <span class="info-label">拥有技能</span>
            <span class="info-value" id="jineng">N/A</span>
        </div>
    </div>

    <!-- 天命主角 -->
    <div class="panel-block">
        <h3 class="panel-title">天命主角</h3>
        <div id="tianmingzhujue-list" class="info-grid"></div>
    </div>

    <!-- 女性角色 -->
    <div class="panel-block">
        <h3 class="panel-title">女性角色</h3>
        <div id="nvxingjuese-list" class="info-grid"></div>
    </div>

    <!-- 当前任务 -->
    <div class="panel-block">
        <h3 class="panel-title">当前任务</h3>
        <div class="task-list">
            <div class="task-item">
                <div class="task-header">
                    <span class="task-name" id="task-name">N/A</span>
                </div>
                <p class="task-desc" id="task-reward">N/A</p>
                <p class="task-desc" id="task-deadline">N/A</p>
            </div>
        </div>
    </div>
</div>

<script>
    function SafeGetValue(obj, path, defaultValue = "N/A") {
        let keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !current.hasOwnProperty(keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        if (current === undefined || current === null) {
            return defaultValue;
        }
        if (Array.isArray(current)) {
            return current.join(', ') || defaultValue;
        }
        return current;
    }

    function updateProgressBar(barId, valueMainId, rawValue, max) {
        const progressBar = document.getElementById(barId);
        const valueMainDisplay = document.getElementById(valueMainId);
        if (!progressBar || !valueMainDisplay) {
            return;
        }
        valueMainDisplay.innerText = rawValue;
        const percentage = (parseFloat(rawValue) / max) * 100;
        progressBar.style.width = `${percentage}%`;
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            const gameData = messages[0].data;
            const characterData = gameData.stat_data;

            // 反派值
            updateProgressBar('fanpaizhi-bar', 'fanpaizhi', SafeGetValue(characterData, '反派值', 0), 1000000);

            // 物品和技能
            document.getElementById('wupin').innerText = SafeGetValue(characterData, '当前拥有的物品', '无');
            document.getElementById('jineng').innerText = SafeGetValue(characterData, '当前拥有的技能', '无');

            // 天命主角
            const tianmingzhujueList = document.getElementById('tianmingzhujue-list');
            let tianmingzhujueHtml = '';
            const tianmingzhujue = SafeGetValue(characterData, '天命主角', {});
            for (const name in tianmingzhujue) {
                tianmingzhujueHtml += `<div class="info-card"><span class="info-label">${name}</span><span class="info-value">气运: ${SafeGetValue(tianmingzhujue, `${name}.气运值`, 0)} | 流派: ${SafeGetValue(tianmingzhujue, `${name}.流派`, '无')}</span></div>`;
            }
            tianmingzhujueList.innerHTML = tianmingzhujueHtml;

            // 女性角色
            const nvxingjueseList = document.getElementById('nvxingjuese-list');
            let nvxingjueseHtml = '';
            const nvxingjuese = SafeGetValue(characterData, '女性角色', {});
            for (const name in nvxingjuese) {
                nvxingjueseHtml += `<div class="info-card"><span class="info-label">${name}</span><span class="info-value">关系: ${SafeGetValue(nvxingjuese, `${name}.与主角关系`, '无')}</span><p class="info-text">身体状态: ${SafeGetValue(nvxingjuese, `${name}.身体状态`, '无')}</p></div>`;
            }
            nvxingjueseList.innerHTML = nvxingjueseHtml;

            // 当前任务
            document.getElementById('task-name').innerText = SafeGetValue(characterData, '当前任务.任务内容', '无');
            document.getElementById('task-reward').innerText = `奖励: ${SafeGetValue(characterData, '当前任务.任务奖励', '无')}`;
            document.getElementById('task-deadline').innerText = `时限: ${SafeGetValue(characterData, '当前任务.任务时限', '无')}`;

        } catch (error) {
            document.getElementById('status-card').innerHTML = `<div class='error-tip'>状态栏加载出错: ${error.message}</div>`;
        }
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
</body>
</html>
